import React from "react";
import "./PerformancePage.css";

const PerformancePage = () => {
  const events = [
    {
      id: 1,
      title: "Annual Day Performances",
      description:
        "Experience the best performances from our Annual Day celebrations, showcasing the talent and creativity of our students and instructors.",
      videos: [
        "https://via.placeholder.com/300", // Replace with actual video URLs or thumbnails
        "https://via.placeholder.com/300",
      ],
    },
    {
      id: 2,
      title: "State Dance Competitions",
      description:
        "Witness our academy's stellar performances from various state-level dance competitions.",
      videos: [
        "https://via.placeholder.com/300",
        "https://via.placeholder.com/300",
      ],
    },
    {
      id: 3,
      title: "Inter-Academy Competitions",
      description:
        "Our students competing and winning hearts at inter-academy dance events.",
      videos: [
        "https://via.placeholder.com/300",
        "https://via.placeholder.com/300",
      ],
    },
    {
      id: 4,
      title: "Workshops & Flash Mobs",
      description:
        "A glimpse into our fun and creative workshops, along with flash mob events organized by our academy.",
      videos: [
        "https://via.placeholder.com/300",
        "https://via.placeholder.com/300",
      ],
    },
    {
      id: 5,
      title: "Special Performances",
      description:
        "Enjoy some of our most unique and memorable performances from special occasions and events.",
      videos: [
        "https://via.placeholder.com/300",
        "https://via.placeholder.com/300",
      ],
    },
  ];

  return (
    <div className="performance-page">
      <header className="hero">
        <h1>Our Performances</h1>
        <p>Explore the incredible performances by our talented students and instructors.</p>
      </header>

      <section className="events">
        {events.map((event) => (
          <div key={event.id} className="event">
            <h2>{event.title}</h2>
            <p>{event.description}</p>
            <div className="video-gallery">
              {event.videos.map((video, index) => (
                <div key={index} className="video-thumbnail">
                  <img src={video} alt={`${event.title} Video ${index + 1}`} />
                  {/* Replace the img tag with a video player like React Player for actual videos */}
                </div>
              ))}
            </div>
          </div>
        ))}
      </section>
    </div>
  );
};

export default PerformancePage;
